import React, { useState } from "react";
import "./Css/BillAndMap.css";

function BillAndMap() {
  const [isCartOpen, setIsCartOpen] = useState(false); // State to control cart animation

  let bill = [
    {
      key: "1",
      item: "Product 1",
      price: "10.99 $",
    },
    {
      key: "2",
      item: "Product 2",
      price: "3.99 $",
    },
    {
      key: "3",
      item: "Product 3",
      price: "9.99 $",
    },
    {
      key: "4",
      item: "Product 4",
      price: "25.99 $",
    },
  ];

  // Function to toggle the cart
  const toggleCart = () => {
    setIsCartOpen(!isCartOpen);
  };

  return (
    <div
      className={`sub-section sub-sectionRestaurant-BillMap ${
        isCartOpen ? "cart-open" : ""
      }`}
    >
      <h2>Cart</h2>
      <table className="data-table">
        <tbody>
          <tr>
            <th className="column1">Items</th>
            <th className="column2">Price</th>
          </tr>
          {bill.map((item) => (
            <tr key={item.key}>
              <td className="column1">{item.item}</td>
              <td className="column2">{item.price}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <button className="table-button" onClick={toggleCart}>
        Pay
      </button>

      <h5>Google Map</h5>
      <div className="google-map"></div>
    </div>
  );
}

export default BillAndMap;
