import React, { useState, useEffect } from 'react';
import { NavLink } from 'react-router-dom'; // Import NavLink for handling active links
import './Css/NavbarCss.css';

const Navbar = () => {
  const [activeWidth, setActiveWidth] = useState(0);
  const [activeHeight, setActiveHeight] = useState(0);
  const [itemPosTop, setItemPosTop] = useState(0);
  const [itemPosLeft, setItemPosLeft] = useState(0);
  const [collapsed, setCollapsed] = useState(true);

  const handleResize = () => {
    setActiveWidth(0);
    setActiveHeight(0);
    setItemPosTop(0);
    setItemPosLeft(0);
  };

  useEffect(() => {
    if (!collapsed) {
      const activeItem = document.querySelector('.navbar-nav .active');
      setActiveWidth(activeItem.offsetWidth);
      setActiveHeight(activeItem.offsetHeight);
      setItemPosTop(activeItem.offsetTop);
      setItemPosLeft(activeItem.offsetLeft);
    }
  }, [collapsed]);

  const toggleNavbar = () => {
    setCollapsed(!collapsed);
    handleResize();
  };

  return (
    <nav className="navbar navbar-expand-custom navbar-mainbg">
      <a className="navbar-brand navbar-logo" href="/">
        Navbar
      </a>
      <button
        className="navbar-toggler"
        type="button"
        aria-controls="navbarSupportedContent"
        aria-expanded={!collapsed}
        aria-label="Toggle navigation"
        onClick={toggleNavbar}
      >
        <i className="fas fa-bars text-white"></i>
      </button>
      <div
        className={`collapse navbar-collapse ${!collapsed && 'show'}`}
        id="navbarSupportedContent"
      >
        <ul className="navbar-nav ml-auto">
          <div
            className="hori-selector"
            style={{
              top: `${itemPosTop}px`,
              left: `${itemPosLeft}px`,
              height: `${activeHeight}px`,
              width: `${activeWidth}px`,
            }}
          >
            <div className="left"></div>
            <div className="right"></div>
          </div>
          <li className="nav-item">
            <NavLink className="nav-link" to="/">
              <i className="fas fa-tachometer-alt"></i>Dashboard
            </NavLink>
          </li>
          <li className="nav-item">
            <NavLink className="nav-link" to="/">
              <i className="far fa-address-book"></i>Address Book
            </NavLink>
          </li>
          <li className="nav-item">
            <NavLink className="nav-link" to="/">
              <i className="far fa-clone"></i>Components
            </NavLink>
          </li>
          <li className="nav-item">
            <NavLink className="nav-link" to="/">
              <i className="far fa-calendar-alt"></i>Calendar
            </NavLink>
          </li>
          <li className="nav-item">
            <NavLink className="nav-link" to="/">
              <i className="far fa-chart-bar"></i>Charts
            </NavLink>
          </li>
          <li className="nav-item">
            <NavLink className="nav-link" to="/">
              <i className="far fa-copy"></i>Documents
            </NavLink>
          </li>
        </ul>
      </div>
    </nav>
  );
};

export default Navbar;
