import React, { useState , useEffect } from "react";

let DUMMY_RESTAURANT = [];
function RestaurentMainDetails(props) {

  const [Restaurantdetail, setRestaurantdetail] = useState(DUMMY_RESTAURANT);
    const restaurantMainDetails = {
        restaurantName : "Patel Restaurant",
        popularDishes : "Chicken Parmesan. crushed tomato aged provolone mozzarella parmesan rigatoni. Chicken Pesto. sweet basil shaved garlic toasted pine nut crispy caper gigli.",
        restaurantImage : "/images/pizza-1.jpg",
      }

      useEffect(()=>{
        restaurentdet();
    },[]);
    
    let restaurentdet = (props) =>{
      fetch('/api/getRestaurentDetails', {
        method: 'GET',
        body: JSON.stringify(),
        headers: {
            'content-Type' : 'application/json'
        }
    }).then(
      data => {
        console.log(data);
        setRestaurantdetail(data);
    }
    );

    }
  return (
    <div className="sectionRestaurant sectionRestaurant-product ">
      <div className="image-container">
        <img src={process.env.PUBLIC_URL + restaurantMainDetails.restaurantImage} />
      </div>
      <div className="sectionRestaurant-product-content">
        <h1>{restaurantMainDetails.restaurantName}</h1>
        <p>
          <strong>Popular dishes :</strong> {Restaurantdetail.popularDishes}
        </p>
      </div>
    </div>
  );
}

export default RestaurentMainDetails;
