import React, { useState } from 'react';
import './Css/MobileMenu.css'; // Import your CSS file with the necessary styles

function NewMenu() {
  const [isMenuOpen, setIsMenuOpen] = useState(true);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const closeMenu = () => {
    setIsMenuOpen(false);
  };

  const handleMenuItemClick = () => {
    // Handle click on menu items as needed
  };

  const handleSubItemClick = () => {
    closeMenu();
    // Handle click on sub-items as needed
  };

  return (
    <div>MENU
      <div className="menu-btn" onClick={toggleMenu}>
        Menu
      </div>
      {isMenuOpen && (
        <div className="menu">
          <span className="close-btn" onClick={closeMenu}>
            &times;
          </span>
          <ul>
            <li className="menu-item" onClick={handleMenuItemClick}>
              <a href="#">Menu Item 1</a>
              <ul className="sub-menu">
                <li className="sub-item" onClick={handleSubItemClick}>
                  <a href="#">Subitem 1</a>
                </li>
                <li className="sub-item" onClick={handleSubItemClick}>
                  <a href="#">Subitem 2</a>
                </li>
              </ul>
            </li>
            <li className="menu-item" onClick={handleMenuItemClick}>
              <a href="#">Menu Item 2</a>
              <ul className="sub-menu">
                <li className="sub-item" onClick={handleSubItemClick}>
                  <a href="#">Subitem 1</a>
                </li>
                <li className="sub-item" onClick={handleSubItemClick}>
                  <a href="#">Subitem 2</a>
                </li>
              </ul>
            </li>
            {/* Add more menu items if needed */}
          </ul>
        </div>
      )}
    </div>
  );
}

export default NewMenu;
