  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  return (
    <div>hi
      <div className="section section-1">
        <div className="image-container">
          <img src="C:/Users/nikun/OneDrive/Desktop/pizza-1.jpg" alt="Image" />
        </div>
        <div className="section-1-content">
          <h1>Image Title</h1>
          <p>
            This is a brief explanation about the image. This text should fit
            within the section and be visible. If the text is longer, the
            section will dynamically adjust its height to accommodate it. This
