import React, { useState } from 'react';
import './Css/restaurentDetailsCss.css'; // Make sure to import your CSS file

function restaurentDetails() {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  return (
    <div>
      <div className="section section-1">
        <div className="image-container">
          <img src="C:/Users/nikun/OneDrive/Desktop/pizza-1.jpg" alt="Image" />
        </div>
        <div className="section-1-content">
          <h1>Image Title</h1>
          <p>
            This is a brief explanation about the image. This text should fit
            within the section and be visible. If the text is longer, the
            section will dynamically adjust its height to accommodate it. This
            is a brief explanation about the image. This text should fit within
            the section and be visible.
          </p>
        </div>
      </div>
      <div className="section section-2">
        <div className="sub-section sub-section-1">
          <div className={`menu${isMenuOpen ? ' open' : ''}`}>
            {/* ... Menu content */}
          </div>
          <div className="product-list">
            {/* ... Product cards */}
          </div>
        </div>
        <div className="sub-section sub-section-2">
          {/* ... Bill and Google Map */}
        </div>
      </div>
      <button className="menu-button" onClick={toggleMenu}>
        Menu
      </button>
    </div>
  );
}

export default restaurentDetails;
